import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, addDoc, getDocs } from "firebase/firestore";
import {
  getDownloadURL,
  getStorage,
  ref,
  uploadBytesResumable,
} from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>([]);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    paragraph: "",
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "glooyaData"));
      const fetchedData = querySnapshot.docs.map((doc) => doc.data());
      setData(fetchedData);
      console.log(fetchedData);
    };

    fetchData();
  }, [db]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const uploadImage = async () => {
    if (
      !selectedFile ||
      !formData.title ||
      !formData.price ||
      !formData.paragraph
    ) {
      return;
    }

    setIsUploading(true);

    try {
      const storageRef = ref(storage, `images/${selectedFile.name}`);
      const uploadTask = uploadBytesResumable(storageRef, selectedFile);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setUploadProgress(progress);
        },
        (error) => {
          console.error("Error uploading image:", error);
          setIsUploading(false);
        },
        async () => {
          const downloadURL = await getDownloadURL(storageRef);
          await addDoc(collection(db, "glooyaData"), {
            imageURL: downloadURL,
            ...formData,
          });
          console.log("Image uploaded and data stored successfully!");
          setIsUploading(false);
          setSelectedFile(null);
          setFormData({ title: "", price: "", paragraph: "" });
        }
      );
    } catch (error) {
      console.error("Error uploading image and data:", error);
      setIsUploading(false);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    uploadImage();
  };

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>{data && <Glooyot data={data} />}</div>

      <div>
        {!isUploading ? (
          <form onSubmit={handleSubmit}>
            <input type="file" onChange={handleFileChange} />
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={formData.title}
              onChange={handleChange}
            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={formData.price}
              onChange={handleChange}
            />
            <input
              type="text"
              name="paragraph"
              placeholder="Paragraph"
              value={formData.paragraph}
              onChange={handleChange}
            />
            <button type="submit">Upload</button>
          </form>
        ) : (
          <div className="uploading">
            <p>Uploading...</p>
            <progress value={uploadProgress} max={100} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
