import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  collection,
  getDocs,
  DocumentData,
} from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

function App() {
  const [data, setData] = useState<DocumentData[]>();
  const [firstRender, setFirstRender] = useState(true);
  useEffect(() => {
    if (firstRender) {
      const fetchData = async () => {
        // Retrieve the data from Firestore
        const querySnapshot = await getDocs(collection(db, "glooyaData"));

        // Process the data
        const fetchedData = querySnapshot.docs.map((doc) => doc.data());

        // Use the fetched data
        console.log(fetchedData);

        setData(fetchedData);
      };
      fetchData();
    }
    setFirstRender(false);
  });

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>
        {data && data.map((item) => <p key={item.id}>{item.title}</p>)}
        {/* <Glooyot /> */}
      </div>
    </div>
  );
}

export default App;
