import React, { useState, useEffect } from "react";
import {
  getFirestore,
  collection,
  Firestore,
  getDocs,
  FirestoreDataConverter,
  QueryDocumentSnapshot,
} from "firebase/firestore/lite";

import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

interface HushRawIn {
  text: string;
  title: string;
  price: number;
}

interface HushRawOut {
  text: string;
  imgRef: string;
  location: string;
  tags?: string[];
}

export class GlooyaModel {
  constructor(
    public price: number,
    public title: string,
    public text: string
  ) {}

  static fromRawIn(hushRaw: HushRawIn, id: string): GlooyotModel {
    // Timestamp.
    return new GlooyotModel(
      id,
      hushRaw.userId,
      hushRaw.text,
      hushRaw.imgRef,
      hushRaw.time,
      hushRaw.location,
      hushRaw.likes?.length,
      hushRaw.dislikes?.length,
      hushRaw.replies,
      hushRaw.tags
    );
  }

  static toRawOut(hush: GlooyotModel): HushRawOut {
    return {
      text: hush.text,
      imgRef: hush.imgRef,
      location: hush.location,
      tags: hush.tags,
    };
  }
}

const hushConverter: FirestoreDataConverter<GlooyaModel> = {
  toFirestore: (data: GlooyaModel): HushRawOut => {
    return GlooyaModel.toRawOut(data);
  },
  fromFirestore: (snapshot: QueryDocumentSnapshot<HushRawIn>): GlooyaModel => {
    return GlooyaModel.fromRawIn(snapshot.data(), snapshot.id);
  },
};

export enum Api {
  Glooyot = "glooyot",
}

export class GlooyaApi {
  constructor(private db: Firestore) {}

  public async getGlooyot(): Promise<GlooyaModel[]> {
    const glooyotCollection = collection(
      this.db,
      Api.Glooyot
    ).withConverter<GlooyaModel>(hushConverter);
    const snap = await getDocs<GlooyaModel>(hushCollection);

    return snap.docs.map((hush) => hush.data());
  }
}

function App() {
  const [firstRender, setFirstRender] = useState(true);
  useEffect(() => {
    (async () => {
      if (firstRender) {
        console.log("initializing");
        const app = initializeApp(firebaseConfig);
        const db = getFirestore(app);
        const apiService: ApiService = new ApiService(db);

        try {
          const UpdatedUser = await apiService.user?.getUserDetails();
          const hushes = await apiService.hush?.getHushes();
        } catch (e) {
          console.log(e);
        }

        setFirstRender(false);
      }
    })();
  }, []);

  const app = initializeApp(firebaseConfig);
  return (
    <div className="App">
      <header className="App-header"></header>
      <div>
        <Glooyot />
      </div>
    </div>
  );
}

export default App;
