import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  getDocs,
  collection,
  doc,
  deleteDoc,
} from "firebase/firestore";
import { getStorage, ref, deleteObject } from "firebase/storage";
import { Form, Search, Options } from "./components";
import firebaseConfig from "./firebase-config.js";

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [scroll, setScroll] = useState(false);
  const [data, setData] = useState<any[]>([]);
  const [queryString, setQueryString] = useState("");
  const [openForm, setOpenForm] = useState(false);
  const [isRTL, setIsRTL] = useState(false);
  const [editedGlooya, setEditedGlooya] = useState<{
    title: string;
    price: number;
    imageURL1: string;
    imageURL2: string;
    paragraph: string;
    selectOptions?: {
      value: string;
      label: string;
    }[];
    id: string;
    position: boolean;
  }>();
  const [options, setOptions] = useState<
    {
      [x: string]: any;
    }[]
  >();

  const scrollableDivRef = useRef<HTMLDivElement | null>(null);

  const handleScroll = () => {
    if (scrollableDivRef.current) {
      if (scrollableDivRef.current.scrollTop > 0) {
        setScroll(true);
        console.log("scrolling");
      } else if (scrollableDivRef.current.scrollTop === 0) {
        setScroll(false);
        console.log("not scrolling");
      }
    }
  };

  useEffect(() => {
    const scrollableDiv = scrollableDivRef.current;
    if (scrollableDiv) {
      scrollableDiv.addEventListener("scroll", handleScroll);
      return () => {
        scrollableDiv.removeEventListener("scroll", handleScroll);
      };
    }
  }, [setScroll]);

  const fetchData = async () => {
    const querySnapshot = await getDocs(collection(db, "glooyaData"));
    const tags = await getDocs(collection(db, "tags"));

    const fetchedTags = tags.docs.map((doc) => {
      const data = doc.data();
      return data.selectOptions; // Replace 'arrayFieldName' with the actual name of the array field in your document
    });
    setOptions(fetchedTags[0]); // Since there's only one document, the array is in the first position

    const fetchedData = querySnapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    setData(fetchedData);
  };

  useEffect(() => {
    fetchData();
  }, [db]);

  const handleDeleteDocument = async (documentId: string) => {
    // Get the document data
    const documentData = data.find((item) => item.id === documentId);

    // Create references to the image files
    const imageRef1 = ref(storage, documentData.imageURL1);
    const imageRef2 = ref(storage, documentData.imageURL2);

    try {
      // Delete the image files
      await deleteDoc(doc(db, "glooyaData", documentId));
      await Promise.all([deleteObject(imageRef1), deleteObject(imageRef2)]);

      // Delete the document from Firestore

      console.log("Document and images successfully deleted.");
    } catch (error) {
      console.error("Error deleting document and images:", error);
    }

    // Update the state
    setData((prevData) => prevData.filter((item) => item.id !== documentId));
  };

  const handleSortBy = (field: string, order: string) => {
    const sortedData = [...data].sort((a, b) => {
      if (order === "asc") {
        return a[field] - b[field];
      } else if (order === "desc") {
        return b[field] - a[field];
      }
      return 0;
    });

    setData(sortedData);
  };

  const handleInputChange = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setQueryString(e.target.value);
  };

  const lowerCaseQuery = queryString.toLowerCase();
  const GLOOYOT_FILTERED = data.filter((glooya) => {
    const lowerCaseTitle = glooya.title.toLowerCase();
    const lowerCaseParagraph = glooya.paragraph.toLowerCase();
    const matchedTags = glooya.selectOptions?.some(
      (option: { value: string }) =>
        option.value.toLowerCase().includes(lowerCaseQuery)
    );

    return (
      lowerCaseTitle.includes(lowerCaseQuery) ||
      lowerCaseParagraph.includes(lowerCaseQuery) ||
      matchedTags
    );
  });

  return (
    <div className={`App \${isRTL ? "rtl" : ""}`}>
      <header className="App-header"></header>
      <div className="body">
        <div className="menu">
          <button onClick={() => setOpenForm(true)} className="new-glooya">
            +
          </button>
          <Options
            isRTL={isRTL}
            handleSortBy={handleSortBy}
            setIsRTL={setIsRTL}
          />
        </div>
        <div className="content">
          {editedGlooya && (
            <Form
              db={db}
              storage={storage}
              setData={setData}
              editedGlooya={editedGlooya}
              setEditedGlooya={setEditedGlooya}
              setOpenForm={setOpenForm}
              updateData={fetchData}
              setOptions={setOptions}
              options={options}
            />
          )}
          {openForm && (
            <Form
              db={db}
              storage={storage}
              setData={setData}
              setOpenForm={setOpenForm}
              updateData={fetchData}
              setOptions={setOptions}
              options={options}
            />
          )}
          {!editedGlooya && !openForm && (
            <>
              <div className={`top-nav ${scroll && "scroll"}`}>
                <h1>Glooyot</h1>
                <Search
                  onChange={handleInputChange}
                  isRTL={isRTL}
                  value={queryString}
                  options={options}
                />
              </div>
              <div>
                {data && (
                  <Glooyot
                    data={GLOOYOT_FILTERED}
                    deleteID={handleDeleteDocument}
                    setEditedGlooya={setEditedGlooya}
                    scrollableDivRef={scrollableDivRef}
                  />
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
