import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  getDocs,
  collection,
  doc,
  deleteDoc,
} from "firebase/firestore";
import { getStorage } from "firebase/storage";
import { Form, Search } from "./components";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>([]);
  const [queryString, setQueryString] = useState("");
  const [openForm, setOpenForm] = useState<boolean>(false);

  const GLOOYOT_FILTERED = data.filter(
    (glooya: { title: string; paragraph: string; tags: string[] }) =>
      glooya.title
        .toLocaleLowerCase()
        .includes(queryString.toLocaleLowerCase()) ||
      glooya.paragraph
        .toLocaleLowerCase()
        .includes(queryString.toLocaleLowerCase()) ||
      (glooya.tags &&
        glooya.tags.map((tag, i) =>
          tag.toLocaleLowerCase().includes(queryString.toLocaleLowerCase())
        ))
  );

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "glooyaData"));
      const fetchedData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setData(fetchedData);
      console.log(fetchedData);
    };

    fetchData();
  }, [db]);

  const handleDeleteDocument = async (documentId: string) => {
    try {
      await deleteDoc(doc(db, "glooyaData", documentId));
      console.log("Document successfully deleted.");
    } catch (error) {
      console.error("Error deleting document:", error);
    }

    setData((prevData) => prevData.filter((item) => item.id !== documentId));
  };

  return (
    <div className="App">
      <header className="App-header"></header>

      {openForm ? (
        <div className="form-wrapper">
          <Form db={db} storage={storage} data={data} setData={setData} />
          <button onClick={() => setOpenForm(false)}></button>
        </div>
      ) : (
        <button onClick={() => setOpenForm(true)}>Form</button>
      )}
      <Search onChange={(e: any) => setQueryString(e.target.value)} />
      <div>
        {data && (
          <Glooyot data={GLOOYOT_FILTERED} deleteID={handleDeleteDocument} />
        )}
      </div>
    </div>
  );
}

export default App;
