import { useState } from "react";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { updateDoc, doc } from "firebase/firestore";
import MyInput from "../MyInput/MyInput";

interface EditFormProps {
  editedData: any;
  setEditedData: (data: any) => void;
  db: any;
  storage: any;
}

const EditForm = ({
  editedData,
  setEditedData,
  db,
  storage,
}: EditFormProps) => {
  const [formData, setFormData] = useState({
    title: editedData.title || "",
    price: editedData.price || "",
    paragraph: editedData.paragraph || "",
    tags: editedData.tags ? editedData.tags.join(",") : "",
  });

  const [selectedFile1, setSelectedFile1] = useState<File | null>(null);
  const [selectedFile2, setSelectedFile2] = useState<File | null>(null);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleFileChange =
    (fileType: string) => (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files && event.target.files[0];

      if (fileType === "file1") {
        setSelectedFile1(file);
      } else if (fileType === "file2") {
        setSelectedFile2(file);
      }
    };

  const updateImages = async () => {
    try {
      const updatedData: any = {};

      // Update the fields other than the images
      updatedData.title = formData.title;
      updatedData.price = formData.price;
      updatedData.paragraph = formData.paragraph;
      updatedData.tags = formData.tags.split(",");

      if (selectedFile1) {
        const storageRef1 = ref(storage, `images/${selectedFile1.name}`);
        const uploadTask1 = uploadBytesResumable(storageRef1, selectedFile1);

        await new Promise<string>((resolve, reject) => {
          uploadTask1.on(
            "state_changed",
            (snapshot) => {
              const progress =
                (snapshot.bytesTransferred / snapshot.totalBytes) * 100;

              // Update progress if needed
            },
            (error) => {
              console.error("Error uploading image 1:", error);
              reject(error);
            },
            async () => {
              const downloadURL = await getDownloadURL(storageRef1);
              resolve(downloadURL);
            }
          );
        });

        updatedData.imageURL1 = await getDownloadURL(storageRef1);
      }

      if (selectedFile2) {
        const storageRef2 = ref(storage, `images/${selectedFile2.name}`);
        const uploadTask2 = uploadBytesResumable(storageRef2, selectedFile2);

        await new Promise<string>((resolve, reject) => {
          uploadTask2.on(
            "state_changed",
            (snapshot) => {
              const progress =
                (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
              // Update progress if needed
            },
            (error) => {
              console.error("Error uploading image 2:", error);
              reject(error);
            },
            async () => {
              const downloadURL = await getDownloadURL(storageRef2);
              resolve(downloadURL);
            }
          );
        });

        updatedData.imageURL2 = await getDownloadURL(storageRef2);
      }

      await updateDoc(doc(db, "glooyaData", editedData.id), updatedData);

      setEditedData((prevData: any) => ({
        ...prevData,
        ...updatedData,
      }));

      console.log("Images and data updated successfully!");
      setEditedData("");
    } catch (error) {
      console.error("Error updating images and data:", error);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    updateImages();
  };

  return (
    <div className="form-outer">
      <div className="form-wrapper">
        <form onSubmit={handleSubmit}>
          <div className="form">
            <div className="form-left form-column">
              <MyInput
                name="title"
                value={formData.title}
                onChange={handleChange}
                isRequired
              />
              <MyInput
                name="price"
                value={formData.price}
                onChange={handleChange}
                isRequired
              />
              <MyInput
                name="paragraph"
                value={formData.paragraph}
                onChange={handleChange}
                isRequired
              />
            </div>
            <div className="form-right form-column">
              <MyInput
                name="tags"
                value={formData.tags}
                onChange={handleChange}
                isRequired
              />
              <input type="file" onChange={handleFileChange("file1")} />
              <input type="file" onChange={handleFileChange("file2")} />
            </div>
          </div>
          <button type="submit" className="submit-form button-30">
            Update
          </button>
          <button
            onClick={() => {
              setEditedData("");
            }}
            className="button-30 center"
          >
            Close
          </button>
        </form>
      </div>
    </div>
  );
};

export default EditForm;
