import React, { FC, useState } from "react";
import "./Glooya.css";
import one from "../../assets/one.jpg";
import oneBack from "../../assets/oneBack.jpg";

type GlooyaProps = {
  glooya: {
    title: string;
    text: string;
    imgFront: string;
    imgBack: string;
    price: number;
  }[];
};

const Glooya: FC<GlooyaProps> = ({ glooya }) => {
  const [flip, setFlip] = useState(false);

  return (
    <div className="glooya-wrapper">
      <div className="glooya-glooya" onClick={() => setFlip(!flip)}>
        <div className={`glooya-inner ${flip && "isFlipped"}`}>
          <div className="glooya-content glooya-front">
            <img src={one} alt="" />
          </div>
          <div className="glooya-content glooya-back">
            <img src={oneBack} alt="" />
          </div>
        </div>
      </div>
      <div className="glooya-text">
        <h1>Shem</h1>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Eveniet non
          explicabo amet vel esse, consectetur, voluptas architecto nemo
          assumenda pariatur sequi ullam sapiente vero nesciunt asperiores
          corrupti, unde facilis harum?
        </p>
      </div>
    </div>
  );
};

export default Glooya;
