import React, { FC, useState, useEffect } from "react";
import "./Form.css";
import {
  FirebaseStorage,
  getDownloadURL,
  ref,
  uploadBytesResumable,
} from "firebase/storage";
import {
  addDoc,
  collection,
  serverTimestamp,
  doc,
  updateDoc,
  Firestore,
} from "firebase/firestore";
import { MyInput } from "..";

type FormProps = {
  db: Firestore;
  storage: FirebaseStorage;
  setData: React.Dispatch<React.SetStateAction<any[]>>;
  editedGlooya?: any;
  setEditedGlooya?: any;
  setOpenForm: React.Dispatch<React.SetStateAction<boolean>>;
  updateData: () => void;
};

const Form: FC<FormProps> = ({
  db,
  storage,
  setData,
  editedGlooya,
  setEditedGlooya,
  setOpenForm,
  updateData,
}) => {
  //   const [selectedFile1, setSelectedFile1] = useState<File | null>(null);
  //   const [selectedFile2, setSelectedFile2] = useState<File | null>(null);
  const [selectedFiles, setSelectedFiles] = useState<{
    [key: string]: File | null;
  }>({
    file1: null,
    file2: null,
  });
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    paragraph: "",
    tags: [] as string[],
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  useEffect(() => {
    if (editedGlooya) {
      const {
        title = "",
        price = "",
        paragraph = "",
        tags = [],
      } = editedGlooya;
      setFormData({ title, price, paragraph, tags });
    }
  }, [editedGlooya]);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: name === "tags" ? (value ? value.split(",") : []) : value,
    }));
  };

  const handleFileChange =
    (name: string) => (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files?.[0] || null;
      setSelectedFiles((prevSelectedFiles) => ({
        ...prevSelectedFiles,
        [name]: file,
      }));
    };

  const uploadImage = async (file: File | null, name: string) => {
    if (!file) {
      return editedGlooya ? editedGlooya[name] : "";
    }
    const timestamp = new Date().getTime();
    const filename = `${timestamp}-${file.name}`; // Append timestamp to the filename

    const storageRef = ref(storage, `images/${filename}`);
    const uploadTask = uploadBytesResumable(storageRef, file);

    return new Promise<string>((resolve, reject) => {
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setUploadProgress(progress);
        },
        (error) => {
          console.error(`Error uploading ${name}:`, error);
          reject(error);
        },
        async () => {
          const downloadURL = await getDownloadURL(storageRef);
          resolve(downloadURL);
        }
      );
    });
  };

  const uploadImages = async () => {
    setIsUploading(true);

    try {
      const downloadURL1 = await uploadImage(selectedFile1, "imageURL1");
      const downloadURL2 = await uploadImage(selectedFile2, "imageURL2");

      const newData = {
        imageURL1: downloadURL1,
        imageURL2: downloadURL2,
        ...formData,
        uploadedAt: serverTimestamp(),
      };

      if (editedGlooya) {
        // Update existing document
        await updateDoc(doc(db, "glooyaData", editedGlooya.id), newData);
        setData((prevData: any) =>
          prevData.map((item: any) =>
            item.id === editedGlooya.id ? newData : item
          )
        );
        setEditedGlooya(null);
      } else {
        // Create new document
        await addDoc(collection(db, "glooyaData"), newData);
        setData((prevData: any) => [...prevData, newData]);
      }

      console.log("Images uploaded and data stored successfully!");
      setOpenForm(false);
      setIsUploading(false);
      setSelectedFile1(null);
      setSelectedFile2(null);
      setEditedGlooya(null);
      updateData();
      setFormData({ title: "", price: "", paragraph: "", tags: [] });
      setUploadProgress(0);
    } catch (error) {
      console.error("Error uploading images and data:", error);
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!editedGlooya) {
      if (!selectedFile1 || !selectedFile2 || !formData.title) {
        alert(
          "Please provide all required fields (title, file1, and file2) for a new upload."
        );
        return;
      }
    }

    uploadImages();
  };

  const handleClose = () => {
    setOpenForm(false);
    if (editedGlooya) {
      setEditedGlooya(null);
    }
  };

  return (
    <div className="form-outer">
      <div className="form-wrapper">
        {!isUploading ? (
          <form onSubmit={handleSubmit}>
            <div className="form">
              <div className="form-left form-column">
                <MyInput
                  name="title"
                  onChange={handleChange}
                  value={formData.title}
                  isRequired
                />
                <MyInput
                  name="price"
                  onChange={handleChange}
                  value={formData.price}
                  isRequired
                />
                <MyInput
                  name="paragraph"
                  onChange={handleChange}
                  value={formData.paragraph}
                  isRequired
                />
              </div>
              <div className="form-right form-column">
                <MyInput
                  name="tags"
                  onChange={handleChange}
                  value={formData.tags.join(",")}
                  isRequired
                />
                <input type="file" onChange={handleFileChange("file1")} />
                <input type="file" onChange={handleFileChange("file2")} />
              </div>
            </div>
            <button type="submit" className="submit-form button-30 ">
              {editedGlooya ? "Update" : "Upload"}
            </button>
            <button onClick={handleClose} className="button-30 center">
              Close
            </button>
          </form>
        ) : (
          <div className="uploading">
            <p>Uploading...</p>
            <progress value={uploadProgress} max={100} />
          </div>
        )}
      </div>
    </div>
  );
};

export default Form;
