import React, { FC, useState } from "react";
import "./Glooya.css";
import one from "../../assets/one.jpg";
import oneBack from "../../assets/oneBack.jpg";

type GlooyaProps = {};

const Glooya: FC<GlooyaProps> = () => {
  const [flip, setFlip] = useState(false);

  return (
    <div className="glooya-wrapper" onClick={() => setFlip(!flip)}>
      <div className="glooya-glooya">
        <div className={`glooya-inner ${flip && "isFlipped"}`}>
          <div className="glooya-content glooya-front">
            <img src={one} alt="" />
          </div>
          <div className="glooya-content glooya-back">
            <img src={oneBack} alt="" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Glooya;
