import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, addDoc, getDocs } from "firebase/firestore";
import {
  getDownloadURL,
  getStorage,
  ref,
  uploadBytesResumable,
} from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);
function App() {
  const [data, setData] = useState<any[]>([]);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "glooyaData"));
      const fetchedData = querySnapshot.docs.map((doc) => doc.data());
      setData(fetchedData);
      console.log(fetchedData);
    };

    fetchData();
  }, [db]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleTitleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTitle(event.target.value);
  };

  const handlePriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setPrice(event.target.value);
  };

  const uploadImage = async () => {
    if (!selectedFile || !title || !price) {
      return;
    }

    setIsUploading(true);

    try {
      const storageRef = ref(storage, `images/${selectedFile.name}`);
      const uploadTask = uploadBytesResumable(storageRef, selectedFile);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setUploadProgress(progress);
        },
        (error) => {
          console.error("Error uploading image:", error);
          setIsUploading(false);
        },
        async () => {
          const downloadURL = await getDownloadURL(storageRef);
          await addDoc(collection(db, "glooyaData"), {
            imageURL: downloadURL,
            title,
            price,
          });
          console.log("Image uploaded and data stored successfully!");
          setIsUploading(false);
          setSelectedFile(null);
          setTitle("");
          setPrice("");
        }
      );
    } catch (error) {
      console.error("Error uploading image and data:", error);
      setIsUploading(false);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    uploadImage();
  };

  const imageUrl =
    "https://firebasestorage.googleapis.com/v0/b/glooyot.appspot.com/o/images%2Fone.jpg?alt=media&token=26ee03f4-4744-4c3d-9bed-b8764f6eb164";

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>{data && <Glooyot data={data} />}</div>

      <div>
        {!isUploading ? (
          <form onSubmit={handleSubmit}>
            <input type="file" onChange={handleFileChange} />
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={handleTitleChange}
            />
            <input
              type="text"
              placeholder="Price"
              value={price}
              onChange={handlePriceChange}
            />
            <button type="submit">Upload</button>
          </form>
        ) : (
          <div className="uploading">
            <p>Uploading...</p>
            <progress value={uploadProgress} max={100} />
          </div>
        )}
      </div>
      <img src={imageUrl} alt="" />
    </div>
  );
}

export default App;
