import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore";
import { getStorage } from "firebase/storage";
import { Form, Search } from "./components";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>([]);
  const [queryString, setQueryString] = useState("");

  const GLOOYOT_FILTERED = data.filter(
    (glooya: { title: string; paragraph: string; tags: string[] }) =>
      glooya.title
        .toLocaleLowerCase()
        .includes(queryString.toLocaleLowerCase()) ||
      glooya.paragraph
        .toLocaleLowerCase()
        .includes(queryString.toLocaleLowerCase()) ||
      glooya.tags.map((tag, i) =>
        tag.toLocaleLowerCase().includes(queryString.toLocaleLowerCase())
      )
  );

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "glooyaData"));
      const fetchedData = querySnapshot.docs.map((doc) => doc.data());
      setData(fetchedData);
      console.log(fetchedData);
    };

    fetchData();
  }, [db]);

  return (
    <div className="App">
      <header className="App-header"></header>
      <Search onChange={(e: any) => setQueryString(e.target.value)} />
      <Form db={db} storage={storage} />
      <div>{data && <Glooyot data={GLOOYOT_FILTERED} />}</div>
    </div>
  );
}

export default App;
