import React, { useState, useEffect } from "react";
import {
  getFirestore,
  collection,
  Firestore,
  getDocs,
} from "firebase/firestore/lite";

import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

export class HushModel {
  constructor(
    public price: number,
    public text: string,
    public title: string
  ) {}
}

export class GlooyaApi {
  constructor(private db: Firestore) {}

  public async getGlooyot(): Promise<HushModel[]> {
    const hushCollection = collection(
      this.db,
      Api.Hushes
    ).withConverter<HushModel>(hushConverter);
    const snap = await getDocs<HushModel>(hushCollection);

    return snap.docs.map((hush) => hush.data());
  }
}

function App() {
  const [firstRender, setFirstRender] = useState(true);
  useEffect(() => {
    (async () => {
      if (firstRender) {
        console.log("initializing");
        const app = initializeApp(firebaseConfig);
        const db = getFirestore(app);
        const apiService: ApiService = new ApiService(db);

        try {
          const UpdatedUser = await apiService.user?.getUserDetails();
          const hushes = await apiService.hush?.getHushes();
        } catch (e) {
          console.log(e);
        }

        setFirstRender(false);
      }
    })();
  }, []);

  const app = initializeApp(firebaseConfig);
  return (
    <div className="App">
      <header className="App-header"></header>
      <div>
        <Glooyot />
      </div>
    </div>
  );
}

export default App;
