import React, { FC, useState } from "react";
import "./OpenImage.css";

interface OpenImageInt {
  openImage: any;
  setOpenImage: any;
}
const OpenImage: FC<OpenImageInt> = ({ openImage, setOpenImage }) => {
  return (
    <div className="open-image-wrapper">
      <button onClick={() => setOpenImage(false)}></button>
      <div>
        <div className={`glooya-inner ${flip && "isFlipped"}`}>
          <div className="glooya-content glooya-front">
            <img src={openImage.img1} alt="" />
          </div>
          <div className="glooya-content glooya-back">
            <img src={openImage.img2} alt="" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default OpenImage;
