import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore";
import { getStorage, ref, uploadBytes } from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>();

  const [firstRender, setFirstRender] = useState(true);
  useEffect(() => {
    if (firstRender) {
      const fetchData = async () => {
        // Retrieve the data from Firestore
        const querySnapshot = await getDocs(collection(db, "glooyaData"));

        // Process the data
        const fetchedData = querySnapshot.docs.map((doc) => doc.data());

        // Use the fetched data
        console.log(fetchedData);

        setData(fetchedData);
      };
      fetchData();
    }
    setFirstRender(false);
  });

  const uploadImage = async (file: any) => {
    // Create a reference to the storage bucket and specify the path to store the image
    const storageRef = ref(storage, "images/" + file.name);

    // Upload the file to the storage bucket
    await uploadBytes(storageRef, file);

    console.log("Image uploaded successfully!");
  };

  const handleFileChange = (event: any) => {
    const file = event.target.files[0];
    uploadImage(file);
  };

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>{data && <Glooyot data={data} />}</div>
      <form action="submit" onSubmit={handleFileChange}>
        <input type="file" />
        <input type="submit" />
      </form>
    </div>
  );
}

export default App;
