import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore";
import {
  getStorage,
  ref,
  uploadBytesResumable,
  UploadTaskSnapshot,
} from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>();
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [firstRender, setFirstRender] = useState(true);

  useEffect(() => {
    if (firstRender) {
      const fetchData = async () => {
        const querySnapshot = await getDocs(collection(db, "glooyaData"));
        const fetchedData = querySnapshot.docs.map((doc) => doc.data());
        setData(fetchedData);
      };
      fetchData();
      setFirstRender(false);
    }
  }, [db, firstRender]);

  const uploadImage = (file: any) => {
    setIsUploading(true);
    const storageRef = ref(storage, "images/" + file.name);

    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
      "state_changed",
      (snapshot: UploadTaskSnapshot) => {
        const progress =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        setUploadProgress(progress);
      },
      (error) => {
        console.error("Error uploading image:", error);
        setIsUploading(false);
      },
      () => {
        console.log("Image uploaded successfully!");
        setIsUploading(false);
      }
    );
  };

  const handleSubmit = (event: any) => {
    event.preventDefault();
    if (selectedFile) {
      uploadImage(selectedFile);
    }
  };

  const handleFileChange = (event: any) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>{data && <Glooyot data={data} />}</div>
      {!uploaded && (
        <div>
          {!isUploading ? (
            <form onSubmit={handleSubmit}>
              <input type="file" onChange={handleFileChange} />
              <button type="submit">Upload</button>
            </form>
          ) : (
            <div>
              <p>Uploading...</p>
              <progress value={uploadProgress} max="100" />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
