import React, { FC, useState, useEffect } from "react";
import "./Form.css";
import {
  FirebaseStorage,
  deleteObject,
  getDownloadURL,
  ref,
  uploadBytesResumable,
} from "firebase/storage";
import {
  addDoc,
  collection,
  serverTimestamp,
  doc,
  updateDoc,
  Firestore,
} from "firebase/firestore";
import { MyInput } from "..";
import CreatableSelect from "react-select/creatable";
import makeAnimated from "react-select/animated";

type FormProps = {
  db: Firestore;
  storage: FirebaseStorage;
  setData: React.Dispatch<React.SetStateAction<any[]>>;
  editedGlooya?: any;
  setEditedGlooya?: any;
  setOpenForm: React.Dispatch<React.SetStateAction<boolean>>;
  updateData: () => void;
  setOptions: React.Dispatch<
    React.SetStateAction<
      | {
          [x: string]: any;
        }[]
      | undefined
    >
  >;
  options:
    | {
        [x: string]: any;
      }[]
    | undefined;
};

const Form: FC<FormProps> = ({
  db,
  storage,
  setData,
  editedGlooya,
  setEditedGlooya,
  setOpenForm,
  updateData,
  setOptions,
  options,
}) => {
  const [selectedFiles, setSelectedFiles] = useState<{
    [key: string]: File | null;
  }>({
    file1: null,
    file2: null,
  });
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    paragraph: "",
    options: [],
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [position, setPosition] = useState<boolean>(false);
  //   const [selectOptions, setSelectOptions] = useState([]);

  //   const animatedComponents = makeAnimated();

  //   useEffect(() => {
  //     console.log(selectOptions);
  //   }, [selectOptions]);

  //   const handleTagChange = (newOption: any) => {
  //     if (newOption && newOption.value && newOption.label) {
  //       setOptions((prevOptions) => {
  //         const optionExists = prevOptions?.find(
  //           (option) => option.value === newOption.value
  //         );
  //         if (!optionExists) {
  //           if (prevOptions) {
  //             return [...prevOptions, newOption];
  //           }
  //         }
  //         return prevOptions;
  //       });
  //     }

  //     setSelectOptions(newOption);
  //   };

  useEffect(() => {
    if (editedGlooya) {
      const {
        title = "",
        price = "",
        paragraph = "",
        options = [],
      } = editedGlooya;
      setFormData({ title, price, paragraph, options });
      setPosition(editedGlooya.position);
    }
  }, [editedGlooya]);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleFileChange =
    (name: string) => (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files?.[0] || null;
      setSelectedFiles((prevSelectedFiles) => ({
        ...prevSelectedFiles,
        [name]: file,
      }));
    };

  const uploadImage = async (file: File | null, name: string) => {
    if (!file) {
      if (editedGlooya && editedGlooya[name]) {
        const oldImageRef = ref(storage, editedGlooya[name]);
        await deleteObject(oldImageRef).catch((error) => {
          console.error(`Error deleting old \\${name}:`, error);
          throw error;
        });
      }
      return editedGlooya ? editedGlooya[name] : "";
    }
    if (!file) {
      return editedGlooya ? editedGlooya[name] : "";
    }
    const timestamp = new Date().getTime();
    const filename = `${timestamp}-${file.name}`;

    if (editedGlooya && editedGlooya[name]) {
      const oldImageRef = ref(storage, editedGlooya[name]);
      await deleteObject(oldImageRef).catch((error) => {
        console.error(`Error deleting old \\${name}:`, error);
        throw error;
      });
    }

    const storageRef = ref(storage, `images/${filename}`);
    const uploadTask = uploadBytesResumable(storageRef, file);

    return new Promise<string>((resolve, reject) => {
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setUploadProgress(progress);
        },
        (error) => {
          console.error(`Error uploading \${name}:`, error);
          alert(
            `An error occurred while uploading \${name}: \${error.message}`
          );
          reject(error);
        },
        async () => {
          const downloadURL = await getDownloadURL(storageRef);
          resolve(downloadURL);
        }
      );
    });
  };

  const uploadImages = async () => {
    setIsUploading(true);

    try {
      const downloadURL1 = await uploadImage(selectedFiles.file1, "imageURL1");
      const downloadURL2 = await uploadImage(selectedFiles.file2, "imageURL2");
      //   await addDoc(collection(db, "tags"), selectOptions);
      console.log("New tag added successfully");
      const newData = {
        imageURL1: downloadURL1,
        imageURL2: downloadURL2,
        position: position,
        ...formData,
        // selectedOptions: selectOptions,
        uploadedAt: serverTimestamp(),
      };

      if (editedGlooya) {
        await updateDoc(doc(db, "glooyaData", editedGlooya.id), newData).catch(
          (error) => {
            console.error("Error updating document:", error);
            alert(
              "An error occurred while updating the document. Please try again."
            );

            throw error;
          }
        );
        setData((prevData: any) =>
          prevData.map((item: any) =>
            item.id === editedGlooya.id ? { ...newData, id: item.id } : item
          )
        );
        setEditedGlooya(null);
      } else {
        await addDoc(collection(db, "glooyaData"), newData).catch((error) => {
          console.error("Error adding document:", error);
          alert(
            "An error occurred while adding a new document. Please try again."
          );

          throw error;
        });
        setData((prevData: any) => [...prevData, newData]);
        updateData();
      }

      console.log("Images uploaded and data stored successfully!");
    } catch (error) {
      console.error("Error uploading images and data:", error);
      alert(
        "An error occurred while uploading images and data. Please try again."
      );
      console.error("Error adding new tag:", error);
      alert("An error occurred while adding a new tag. Please try again.");
    } finally {
      setOpenForm(false);
      setIsUploading(false);
      setSelectedFiles({ file1: null, file2: null });
      setEditedGlooya(null);
      setFormData({ title: "", price: "", paragraph: "", options: [] });
      setUploadProgress(0);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!editedGlooya) {
      if (!selectedFiles || !formData.title) {
        alert(
          "Please provide all required fields (title, file1, and file2) for a new upload."
        );
        return;
      }
    }

    uploadImages();
  };

  const resetForm = () => {
    setSelectedFiles({ file1: null, file2: null });
    setFormData({ title: "", price: "", paragraph: "", options: [] });
    setUploadProgress(0);
    setPosition(false);
  };

  const handleClose = () => {
    setOpenForm(false);
    if (editedGlooya) {
      setEditedGlooya(null);
    }
    resetForm();
  };

  return (
    <div className="form-outer">
      <div className="form-wrapper">
        {!isUploading ? (
          <form onSubmit={handleSubmit}>
            <div className="form">
              <div className="form-left form-column">
                <MyInput
                  name="title"
                  onChange={handleChange}
                  value={formData.title}
                  isRequired
                />
                <MyInput
                  name="price"
                  onChange={handleChange}
                  value={formData.price}
                  isRequired
                />
                <MyInput
                  name="paragraph"
                  onChange={handleChange}
                  value={formData.paragraph}
                  isRequired
                />
              </div>
              <div className="form-right form-column">
                <CreatableSelect
                  closeMenuOnSelect={false}
                  //   components={animatedComponents}
                  placeholder="Select Tags"
                  isMulti
                  options={options}
                  //   onChange={handleTagChange}
                />

                <div className="form-files">
                  <div className="file-container">
                    <label htmlFor="file1">Front Image</label>
                    <input
                      className="file"
                      type="file"
                      onChange={handleFileChange("file1")}
                      id="file1"
                    />
                  </div>

                  <div className="file-container">
                    <label htmlFor="file2">Back Image</label>
                    <input
                      className="file"
                      type="file"
                      onChange={handleFileChange("file2")}
                      id="file2"
                    />
                  </div>
                </div>
              </div>
            </div>
            <div className="posiotions">
              <div
                className={`position ${!position && "on"}`}
                onClick={() => {
                  setPosition(true);
                }}
              >
                <div className="vertical"></div>
              </div>
              <div
                className={`position ${position && "on"}`}
                onClick={() => {
                  setPosition(false);
                }}
              >
                <div className="horizontal"></div>
              </div>
            </div>
            <button type="submit" className="submit-form button-30 ">
              {editedGlooya ? "Update" : "Upload"}
            </button>
            <button onClick={handleClose} className="button-30 center">
              Close
            </button>
          </form>
        ) : (
          <div className="uploading">
            <p>Uploading...</p>
            <progress value={uploadProgress} max={100} />
          </div>
        )}
      </div>
    </div>
  );
};

export default Form;
