import React, { useState, useEffect } from "react";

const App = () => {
  const [timer, setTimer] = useState(null);

  const handleColorChange = (e) => {
    const el = e.target;

    if (el.matches("[data-color]")) {
      clearTimeout(timer);
      const newTimer = setTimeout(() => {
        document.documentElement.style.setProperty(
          `--color-${el.dataset.color}`,
          el.value
        );
      }, 100);
      setTimer(newTimer);
    }
  };

  useEffect(() => {
    document.addEventListener("input", handleColorChange);
    return () => {
      clearTimeout(timer);
      document.removeEventListener("input", handleColorChange);
    };
  }, [timer]);

  return (
    <article className="l-design-widht">
      <h1>Mini CSS-vars Design System</h1>

      <p>
        Most of the projects I work on have about <mark>3</mark> important
        colors: Main- , Accent- and Background-Color. Naturally tons of other
        colors are used in a typical Project, but they are mostly used within
        specific components.
      </p>

      <p>
        I find it useful to set those 3 colors as vars on the root and change
        them in specific contexts. It turns out that the complexity of the
        components I build is dramatically cut down by this. And also gives me a
        lot of control over the cascade.
      </p>

      <div className="card">
        <h2>
          <svg className="icon" aria-hidden="true">
            <use xlinkHref="#icon-coffee" href="#icon-coffee" />
          </svg>
          Regular
        </h2>
        <label className="input">
          <input className="input__field" type="text" placeholder=" " />
          <span className="input__label">Some Fancy Label</span>
        </label>
        <div className="button-group">
          <button>Send</button>
          <button type="reset">Reset</button>
        </div>
      </div>

      <div className="card card--inverted">
        <h2>
          <svg className="icon" aria-hidden="true">
            <use xlinkHref="#icon-coffee" href="#icon-coffee" />
          </svg>
          Inverted
        </h2>
        <label className="input">
          <input
            className="input__field"
            type="text"
            placeholder=" "
            value="Valuable value"
          />
          <span className="input__label">Some Fancy Label</span>
        </label>

        <div className="button-group">
          <button>Send</button>
          <button type="reset">Reset</button>
        </div>
      </div>

      <div className="card card--accent">
        <h2>
          <svg className="icon" aria-hidden="true">
            <use xlinkHref="#icon-coffee" href="#icon-coffee" />
          </svg>
          Accent
        </h2>
        <label className="input">
          <input className="input__field" type="text" placeholder=" " />
          <span className="input__label">Some Fancy Label</span>
        </label>

        <div className="button-group">
          <button>Send</button>
          <button type="reset">Reset</button>
        </div>
      </div>

      <div className="card card--inverted">
        <h2>Colors</h2>
        <p>Play around with the colors</p>
        <input type="color" data-color="light" value="#ffffff" />
        <input type="color" data-color="dark" value="#212121" />
        <input type="color" data-color="signal" value="#fab700" />
      </div>
    </article>
  );
};

export default App;
