import React, { FC, useState } from "react";
import { updateDoc, doc } from "firebase/firestore";
import { MyInput } from "..";

interface EditFormProps {
  db: any;
  editedData: any;
  setEditedData: any;
  handleFormToggle: () => void;
}

interface FormData {
  title: string;
  price: string;
  paragraph: string;
  tags: string[];
}

const EditForm: FC<EditFormProps> = ({
  db,
  editedData,
  setEditedData,
  handleFormToggle,
}) => {
  const [formData, setFormData] = useState<FormData>({
    title: editedData.title,
    price: editedData.price,
    paragraph: editedData.paragraph,
    tags: editedData.tags,
  });

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: name === "tags" ? value.split(",") : value,
    }));
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    try {
      const updatedData = { ...formData };

      await updateDoc(doc(db, "glooyaData", editedData.id), updatedData);

      setEditedData((prevData: any) => ({
        ...prevData,
        ...updatedData,
      }));

      console.log("Data stored successfully!");
      setEditedData("");
      handleFormToggle();
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  return (
    <div className="form-outer">
      <div className="form-wrapper">
        <form onSubmit={handleSubmit}>
          <div className="form">
            <div className="form-left form-column">
              <MyInput
                name="title"
                value={formData.title}
                onChange={handleChange}
                isRequired
              />
              <MyInput
                name="price"
                value={formData.price}
                onChange={handleChange}
                isRequired
              />
              <MyInput
                name="paragraph"
                value={formData.paragraph}
                onChange={handleChange}
                isRequired
              />
            </div>
            <div className="form-right form-column">
              <MyInput
                name="tags"
                value={formData.tags.join(",")}
                onChange={handleChange}
                isRequired
              />
            </div>
          </div>
          <button type="submit" className="submit-form button-30">
            Update
          </button>
          <button
            onClick={() => {
              setEditedData("");
            }}
            className="button-30 center"
          >
            Close
          </button>
        </form>
      </div>
    </div>
  );
};

export default EditForm;
