import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  getDocs,
  collection,
  doc,
  deleteDoc,
} from "firebase/firestore";
import { getStorage } from "firebase/storage";
import { Form, Search, Options, EditGlooya } from "./components";
import firebaseConfig from "./firebase-config.js";

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>([]);
  const [queryString, setQueryString] = useState("");
  const [openForm, setOpenForm] = useState(false);
  const [isRTL, setIsRTL] = useState(false);
  const [editedGlooya, setEditedGlooya] = useState();
  // const [editGlooyaOpen, setEditGlooyaOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "glooyaData"));
      const fetchedData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setData(fetchedData);
    };

    fetchData();
  }, [db]);

  const handleDeleteDocument = async (documentId: string) => {
    try {
      await deleteDoc(doc(db, "glooyaData", documentId));
      console.log("Document successfully deleted.");
    } catch (error) {
      console.error("Error deleting document:", error);
    }

    setData((prevData) => prevData.filter((item) => item.id !== documentId));
  };

  const handleSortBy = (field: string, order: string) => {
    const sortedData = [...data].sort((a, b) => {
      if (order === "asc") {
        return a[field] - b[field];
      } else if (order === "desc") {
        return b[field] - a[field];
      }
      return 0;
    });

    setData(sortedData);
  };

  const handleInputChange = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setQueryString(e.target.value);
  };

  const handleFormToggle = () => {
    setOpenForm((prevState) => !prevState);
  };

  const GLOOYOT_FILTERED = data.filter((glooya) => {
    const lowerCaseQuery = queryString.toLocaleLowerCase();
    const lowerCaseTitle = glooya.title.toLocaleLowerCase();
    const lowerCaseParagraph = glooya.paragraph.toLocaleLowerCase();
    const matchedTags = glooya.tags?.some((tag: string) =>
      tag.toLocaleLowerCase().includes(lowerCaseQuery)
    );

    return (
      lowerCaseTitle.includes(lowerCaseQuery) ||
      lowerCaseParagraph.includes(lowerCaseQuery) ||
      matchedTags
    );
  });

  return (
    <div className={`App ${isRTL ? "rtl" : ""}`}>
      <header className="App-header"></header>
      {editedGlooya && (
        <EditGlooya
          db={db}
          storage={storage}
          editedData={editedGlooya}
          setEditedData={setEditedGlooya}
          handleFormToggle={handleFormToggle}
        />
      )}

      <Search onChange={handleInputChange} isRTL={isRTL} value={queryString} />
      <Options isRTL={isRTL} handleSortBy={handleSortBy} setIsRTL={setIsRTL} />
      {openForm ? (
        <Form
          db={db}
          storage={storage}
          setData={setData}
          handleFormToggle={handleFormToggle}
        />
      ) : (
        <button onClick={handleFormToggle} className="new-glooya button-30 ">
          {isRTL ? "חדש" : "New"}
        </button>
      )}
      <div>
        {data && (
          <Glooyot
            data={GLOOYOT_FILTERED}
            deleteID={handleDeleteDocument}
            setEditedGlooya={setEditedGlooya}
          />
        )}
      </div>
    </div>
  );
}

export default App;
