import React, { FC, useState } from "react";
import "./FormSelector.css";
import { Form } from "../../components";
interface FormSelectorInt {
  editedGlooya:
    | {
        title: string;
        price: number;
        imageURL1: string;
        imageURL2: string;
        paragraph: string;
        selectOptions?:
          | {
              value: string;
              label: string;
            }[]
          | undefined;
        id: string;
        position: boolean;
      }
    | undefined;
  setEditedGlooya: React.Dispatch<
    React.SetStateAction<
      | {
          title: string;
          price: number;
          imageURL1: string;
          imageURL2: string;
          paragraph: string;
          selectOptions?:
            | {
                value: string;
                label: string;
              }[]
            | undefined;
          id: string;
          position: boolean;
        }
      | undefined
    >
  >;
  openForm: boolean;
  setOpenForm: any;
  db: any;
  storage: any;
  setData: any;
  fetchData: any;
  setOptions: any;
  options: any;
}
const FormSelector: FC<FormSelectorInt> = ({
  editedGlooya,
  openForm,
  setOpenForm,
  db,
  storage,
  setData,
  setEditedGlooya,
  fetchData,
  setOptions,
  options,
}) => {
  return (
    <>
      {editedGlooya && (
        <Form
          db={db}
          storage={storage}
          setData={setData}
          editedGlooya={editedGlooya}
          setEditedGlooya={setEditedGlooya}
          setOpenForm={setOpenForm}
          updateData={fetchData}
          setOptions={setOptions}
          options={options}
        />
      )}
      {openForm && (
        <Form
          db={db}
          storage={storage}
          setData={setData}
          setOpenForm={setOpenForm}
          updateData={fetchData}
          setOptions={setOptions}
          options={options}
        />
      )}
    </>
  );
};

export default FormSelector;
