import React, { FC, useState } from "react";
import "./OpenImage.css";

interface OpenImageInt {
  openImage: any;
  setOpenImage: any;
  position: boolean;
}
const OpenImage: FC<OpenImageInt> = ({ openImage, setOpenImage, position }) => {
  const [closing, setClosing] = useState(false);
  const [rotation, setRotation] = useState(0);
  const [front, setFront] = useState(true);

  const handleClick = () => {
    setRotation(rotation + 90);
  };

  const imageStyle = {
    transform: `rotate(${rotation}deg)`,
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(function () {
      setOpenImage(false);
      setClosing(false);
    }, 500);
  };

  return (
    <div className={`open-image-wrapper ${closing && "go-reverse"}`}>
      <button onClick={handleClose}></button>

      <div className="expand-wrapper">
        <button onClick={handleClick}>bababa</button>
        {front ? (
          <div className={`expand-column ${position && "horizontal"}`}>
            <div className="expand-content" style={imageStyle}>
              <img src={openImage.img1} alt="" />
            </div>
          </div>
        ) : (
          <div className={`expand-column ${position && "horizontal"}`}>
            <div className="expand-content" style={imageStyle}>
              <img src={openImage.img2} alt="" />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default OpenImage;
