import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, addDoc, getDocs } from "firebase/firestore";
import {
  getDownloadURL,
  getStorage,
  ref,
  uploadBytesResumable,
} from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const storage = getStorage(app);

function App() {
  const [data, setData] = useState<any[]>([]);
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    paragraph: "",
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "glooyaData"));
      const fetchedData = querySnapshot.docs.map((doc) => doc.data());
      setData(fetchedData);
      console.log(fetchedData);
    };

    fetchData();
  }, [db]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      const files = Array.from(event.target.files);
      setSelectedFiles(files);
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const uploadImages = async () => {
    if (
      selectedFiles.length !== 2 ||
      !formData.title ||
      !formData.price ||
      !formData.paragraph
    ) {
      return;
    }

    setIsUploading(true);

    try {
      const storageRefs = selectedFiles.map((file) =>
        ref(storage, `images/${file.name}`)
      );
      const uploadTasks = selectedFiles.map((file, index) =>
        uploadBytesResumable(storageRefs[index], file)
      );

      const uploadPromises = uploadTasks.map(
        (uploadTask) =>
          new Promise<string>((resolve, reject) => {
            uploadTask.on(
              "state_changed",
              (snapshot) => {
                const progress =
                  (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                setUploadProgress(progress);
              },
              (error) => {
                console.error("Error uploading image:", error);
                reject(error);
              },
              async () => {
                const downloadURL = await getDownloadURL(
                  uploadTask.snapshot.ref
                );
                resolve(downloadURL);
              }
            );
          })
      );

      const [downloadURL1, downloadURL2] = await Promise.all(uploadPromises);

      await addDoc(collection(db, "glooyaData"), {
        imageURL1: downloadURL1,
        imageURL2: downloadURL2,
        ...formData,
      });

      console.log("Images uploaded and data stored successfully!");
      setIsUploading(false);
      setSelectedFiles([]);
      setFormData({ title: "", price: "", paragraph: "" });
      setUploadProgress(0);
    } catch (error) {
      console.error("Error uploading images and data:", error);
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    uploadImages();
  };
  return (
    <div className="App">
      <header className="App-header"></header>
      <div>{data && <Glooyot data={data} />}</div>

      <div>
        {!isUploading ? (
          <form onSubmit={handleSubmit}>
            <input type="file" onChange={handleFileChange} multiple />
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={formData.title}
              onChange={handleChange}
            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={formData.price}
              onChange={handleChange}
            />
            <input
              type="text"
              name="paragraph"
              placeholder="Paragraph"
              value={formData.paragraph}
              onChange={handleChange}
            />
            <button type="submit">Upload</button>
          </form>
        ) : (
          <div className="uploading">
            <p>Uploading...</p>
            <progress value={uploadProgress} max={100} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
