import React, { useState, useEffect } from "react";
import {
  getFirestore,
  collection,
  Firestore,
  getDocs,
  FirestoreDataConverter,
  QueryDocumentSnapshot,
} from "firebase/firestore/lite";

import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

export class ApiService {
  public glooya?: GlooyaApi;

  constructor(private db: Firestore) {
    this.glooya = new GlooyaApi(db);
  }
}

interface GlooyaRawIn {
  text: string;
  title: string;
  price: number;
}

interface GlooyaRawOut {
  text: string;
  title: string;
  price: number;
}

export class GlooyaModel {
  constructor(
    public price: number,
    public title: string,
    public text: string
  ) {}

  static fromRawIn(glooyaRaw: GlooyaRawIn, id: string): GlooyaModel {
    return new GlooyaModel(glooyaRaw.price, glooyaRaw.text, glooyaRaw.title);
  }

  static toRawOut(glooya: GlooyaModel): GlooyaRawOut {
    return {
      text: glooya.text,
      title: glooya.title,
      price: glooya.price,
    };
  }
}

const glooyaConverter: FirestoreDataConverter<GlooyaModel> = {
  toFirestore: (data: GlooyaModel): GlooyaRawOut => {
    return GlooyaModel.toRawOut(data);
  },
  fromFirestore: (
    snapshot: QueryDocumentSnapshot<GlooyaRawIn>
  ): GlooyaModel => {
    return GlooyaModel.fromRawIn(snapshot.data(), snapshot.id);
  },
};

export enum Api {
  Glooyot = "glooyot",
}

export class GlooyaApi {
  constructor(private db: Firestore) {}

  public async getGlooyot(): Promise<GlooyaModel[]> {
    const glooyotCollection = collection(
      this.db,
      Api.Glooyot
    ).withConverter<GlooyaModel>(glooyaConverter);
    const snap = await getDocs<GlooyaModel>(glooyotCollection);

    return snap.docs.map((glooya) => glooya.data());
  }
}

function App() {
  const [firstRender, setFirstRender] = useState(true);
  useEffect(() => {
    (async () => {
      if (firstRender) {
        console.log("initializing");
        const app = initializeApp(firebaseConfig);
        const db = getFirestore(app);
        const apiService: ApiService = new ApiService(db);

        try {
          const glooyot = await apiService.glooya?.getGlooyot();
          console.log(glooyot);
        } catch (e) {
          console.log(e);
        }

        setFirstRender(false);
      }
    })();
  }, []);

  const app = initializeApp(firebaseConfig);
  return (
    <div className="App">
      <header className="App-header"></header>
      <div>
        <Glooyot />
      </div>
    </div>
  );
}

export default App;
