import React, { useState, useEffect } from "react";
import "./App.css";
import { Glooyot } from "./containers";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyC77kxJju0Sknm-FoO1kPpK6_MInpRvg1s",
  authDomain: "glooyot.firebaseapp.com",
  projectId: "glooyot",
  storageBucket: "glooyot.appspot.com",
  messagingSenderId: "147431531845",
  appId: "1:147431531845:web:948c2e0148c105420eca2c",
};

function App() {
  const app = initializeApp(firebaseConfig);
  const db = getFirestore(app);
  const [data, setData] = useState<firebase.firestore.DocumentData[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Retrieve the data from Firestore
        const querySnapshot = await getDocs(collection(db, "yourCollection"));

        // Process the data
        const fetchedData = querySnapshot.docs.map((doc) => doc.data());

        // Update the state with the fetched data
        setData(fetchedData);
      } catch (error) {
        console.log("Error getting data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>
        {/* {data.map((item) => (
          <p key={item.id}>{item.title}</p>
        ))} */}
        {/* <Glooyot /> */}
      </div>
    </div>
  );
}

export default App;
