import React, { FC, useState } from "react";
import "./Form.css";
import { getDownloadURL, ref, uploadBytesResumable } from "firebase/storage";
import { addDoc, collection } from "firebase/firestore";

type FormProps = { db: any; storage: any };

const Form: FC<FormProps> = ({ db, storage }) => {
  const [selectedFile1, setSelectedFile1] = useState<File | null>(null);
  const [selectedFile2, setSelectedFile2] = useState<File | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    paragraph: "",
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const handleFileChange1 = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile1(event.target.files[0]);
    }
  };

  const handleFileChange2 = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile2(event.target.files[0]);
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const uploadImages = async () => {
    if (
      !selectedFile1 ||
      !selectedFile2 ||
      !formData.title ||
      !formData.price ||
      !formData.paragraph
    ) {
      return;
    }

    setIsUploading(true);

    try {
      const storageRef1 = ref(storage, `images/${selectedFile1.name}`);
      const storageRef2 = ref(storage, `images/${selectedFile2.name}`);

      const uploadTask1 = uploadBytesResumable(storageRef1, selectedFile1);
      const uploadTask2 = uploadBytesResumable(storageRef2, selectedFile2);

      const uploadPromise1 = new Promise((resolve, reject) => {
        uploadTask1.on(
          "state_changed",
          (snapshot: { bytesTransferred: number; totalBytes: number }) => {
            const progress =
              (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            setUploadProgress(progress);
          },
          (error: any) => {
            console.error("Error uploading image 1:", error);
            reject(error);
          },
          async () => {
            resolve(await getDownloadURL(storageRef1));
          }
        );
      });

      const uploadPromise2 = new Promise((resolve, reject) => {
        uploadTask2.on(
          "state_changed",
          (snapshot: { bytesTransferred: number; totalBytes: number }) => {
            const progress =
              (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            setUploadProgress(progress);
          },
          (error: any) => {
            console.error("Error uploading image 2:", reject(error));
          },
          async () => {
            resolve(await getDownloadURL(storageRef2));
          }
        );
      });

      const [downloadURL1, downloadURL2] = await Promise.all([
        uploadPromise1,
        uploadPromise2,
      ]);

      await addDoc(collection(db, "glooyaData"), {
        imageURL1: downloadURL1,
        imageURL2: downloadURL2,
        ...formData,
      });

      console.log("Images uploaded and data stored successfully!");
      setIsUploading(false);
      setSelectedFile1(null);
      setSelectedFile2(null);
      setFormData({ title: "", price: "", paragraph: "" });
      setUploadProgress(0);
    } catch (error) {
      console.error("Error uploading images and data:", error);
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    uploadImages();
  };
  return (
    <div className="form-wrapper">
      {!isUploading ? (
        <form onSubmit={handleSubmit}>
          <input type="file" onChange={handleFileChange1} />
          <input type="file" onChange={handleFileChange2} />
          <input
            type="text"
            name="title"
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
          />
          <input
            type="text"
            name="price"
            placeholder="Price"
            value={formData.price}
            onChange={handleChange}
          />
          <input
            type="text"
            name="paragraph"
            placeholder="Paragraph"
            value={formData.paragraph}
            onChange={handleChange}
          />
          <button type="submit">Upload</button>
        </form>
      ) : (
        <div className="uploading">
          <p>Uploading...</p>
          <progress value={uploadProgress} max={100} />
        </div>
      )}
    </div>
  );
};

export default Form;
