import React, { FC } from "react";
import "./Options.css";
interface OptionsInt {
  isRTL: boolean;
  setIsRTL: React.Dispatch<React.SetStateAction<boolean>>;
  handleSortBy: (field: string, order: string) => void;
}
const Options: FC<OptionsInt> = ({ isRTL, setIsRTL, handleSortBy }) => {
  const toggleRTL = () => {
    setIsRTL((prevValue) => !prevValue);
  };

  return (
    <div className="options">
      <button
        onClick={() => [
          handleSortBy("uploadedAt", "desc"),
          () => {
            window.scrollTo(0, 0);
          },
        ]}
        className="button-17"
      >
        {isRTL ? "מיין לפי תאריך" : "Sort By Date"}
      </button>
      <button
        onClick={() => handleSortBy("price", "asc")}
        className="button-17"
      >
        {isRTL ? "מיין לפי מחיר עולה" : "Sort By Ascending Price"}
      </button>
      <button
        onClick={() => handleSortBy("price", "desc")}
        className="button-17"
      >
        {isRTL ? "מיין לפי מחיר יורד" : "Sort By Descending Price"}
      </button>
      <button onClick={toggleRTL} className="button-17 lang">
        {isRTL ? "אנגלית" : "Hebrew"}
      </button>
    </div>
  );
};

export default Options;
