import React, { FC } from "react";
import "./Options.css";
interface OptionsInt {
  isRTL: boolean;
  setIsRTL: React.Dispatch<React.SetStateAction<boolean>>;
  handleSortBy: (field: string, order: string) => void;
}
const Options: FC<OptionsInt> = ({ isRTL, setIsRTL, handleSortBy }) => {
  const toggleRTL = () => {
    setIsRTL((prevValue) => !prevValue);
  };

  return (
    <div className="options">
      <button onClick={() => handleSortBy("uploadedAt", "desc")}>
        {isRTL ? "מיין לפי תאריך" : "Sort By Date"}
      </button>
      <button onClick={() => handleSortBy("price", "asc")}>
        {isRTL ? "מיין לפי מחיר עולה" : "Sort By Ascending Price"}
      </button>
      <button onClick={() => handleSortBy("price", "desc")}>
        {isRTL ? "מיין לפי מחיר יורד" : "Sort By Descending Price"}
      </button>
      <button onClick={toggleRTL}>{isRTL ? "אנגלית" : "Hebrew"}</button>
    </div>
  );
};

export default Options;
