import React, { FC, useState } from "react";
import "./MyInput.css"; // Import the CSS file for styling

type MyInputProps = {
  name: string;
  onChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
};

const CoolInput: FC<MyInputProps> = ({ name, onChange }) => {
  const [value, setValue] = useState("");
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  const change = (event: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setValue(event.target.value);
    onChange;
  };

  return (
    <div className={`cool-input ${isFocused ? "focused" : ""}`}>
      <input
        type="text"
        name={name}
        value={value}
        onChange={onChange}
        onFocus={handleFocus}
        onBlur={handleBlur}
      />
      <label className={isFocused || value ? "label-out" : ""}>
        Enter text here...
      </label>
      <div className="underline"></div>
    </div>
  );
};

export default CoolInput;
