import React, { FC, useState } from "react";
import "./Form.css";
import { getDownloadURL, ref, uploadBytesResumable } from "firebase/storage";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { MyInput } from "..";

type FormProps = {
  db: any;
  storage: any;
  setData: any;
  handleFormToggle: () => void;
};

const Form: FC<FormProps> = ({ db, storage, setData, handleFormToggle }) => {
  const [selectedFile1, setSelectedFile1] = useState<File | null>(null);
  const [selectedFile2, setSelectedFile2] = useState<File | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    paragraph: "",
    tags: [] as string[],
  });

  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: name === "tags" ? value.split(",") : value,
    }));
  };

  const handleFileChange =
    (name: string) => (event: React.ChangeEvent<HTMLInputElement>) => {
      if (event.target.files && event.target.files.length > 0) {
        if (name === "file1") {
          setSelectedFile1(event.target.files[0]);
        } else if (name === "file2") {
          setSelectedFile2(event.target.files[0]);
        }
      }
    };

  const uploadImages = async () => {
    if (
      !selectedFile1 ||
      !selectedFile2 ||
      Object.values(formData).some((value) => value === "")
    ) {
      return;
    }

    setIsUploading(true);

    try {
      const storageRef1 = ref(storage, `images/${selectedFile1.name}`);
      const storageRef2 = ref(storage, `images/${selectedFile2.name}`);

      const uploadTask1 = uploadBytesResumable(storageRef1, selectedFile1);
      const uploadTask2 = uploadBytesResumable(storageRef2, selectedFile2);

      const uploadPromise1 = new Promise<string>((resolve, reject) => {
        uploadTask1.on(
          "state_changed",
          (snapshot) => {
            const progress =
              (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            setUploadProgress(progress);
          },
          (error) => {
            console.error("Error uploading image 1:", error);
            reject(error);
          },
          async () => {
            const downloadURL = await getDownloadURL(storageRef1);
            resolve(downloadURL);
          }
        );
      });

      const uploadPromise2 = new Promise<string>((resolve, reject) => {
        uploadTask2.on(
          "state_changed",
          (snapshot) => {
            const progress =
              (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            setUploadProgress(progress);
          },
          (error) => {
            console.error("Error uploading image 2:", error);
            reject(error);
          },
          async () => {
            const downloadURL = await getDownloadURL(storageRef2);
            resolve(downloadURL);
          }
        );
      });

      const [downloadURL1, downloadURL2] = await Promise.all([
        uploadPromise1,
        uploadPromise2,
      ]);

      const newData = {
        imageURL1: downloadURL1,
        imageURL2: downloadURL2,

        ...formData,
        uploadedAt: serverTimestamp(), // Include the current timestamp
      };

      await addDoc(collection(db, "glooyaData"), newData);

      setData((prevData: any) => [...prevData, newData]);

      console.log("Images uploaded and data stored successfully!");
      handleFormToggle();
      setIsUploading(false);
      setSelectedFile1(null);
      setSelectedFile2(null);
      setFormData({ title: "", price: "", paragraph: "", tags: [] });
      setUploadProgress(0);
    } catch (error) {
      console.error("Error uploading images and data:", error);
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    uploadImages();
  };

  return (
    <div className="form-outer">
      <div className="form-wrapper">
        {!isUploading ? (
          <form onSubmit={handleSubmit}>
            <div className="form">
              <div className="form-left form-column">
                <MyInput
                  name="title"
                  onChange={handleChange}
                  value={formData.title}
                  isRequired
                />
                <MyInput
                  name="price"
                  onChange={handleChange}
                  value={formData.price}
                  isRequired
                />
                <MyInput
                  name="paragraph"
                  onChange={handleChange}
                  value={formData.paragraph}
                  isRequired
                />
              </div>
              <div className="form-right form-column">
                <MyInput
                  name="tags"
                  onChange={handleChange}
                  value={formData.tags.join(",")}
                  isRequired
                />

                <input type="file" onChange={handleFileChange("file1")} />
                <input type="file" onChange={handleFileChange("file2")} />
              </div>
            </div>
            <button type="submit" className="submit-form button-30 ">
              Upload
            </button>
            <button onClick={handleFormToggle} className="button-30 center">
              Close
            </button>
          </form>
        ) : (
          <div className="uploading">
            <p>Uploading...</p>
            <progress value={uploadProgress} max={100} />
          </div>
        )}
      </div>
    </div>
  );
};

export default Form;
