import React, { FC } from "react";
import "./Options.css";
interface OptionsInt {
  isRTL: boolean;
  setIsRTL: React.Dispatch<React.SetStateAction<boolean>>;
  handleSortBy: (field: string, order: string) => void;
}
const Options: FC<OptionsInt> = ({ isRTL, setIsRTL, handleSortBy }) => {
  const toggleRTL = () => {
    setIsRTL((prevValue) => !prevValue);
  };

  return (
    <div className="options">
      <button
        className="sort-button"
        onClick={() => [
          handleSortBy("uploadedAt", "desc"),
          () => {
            window.scrollTo(0, 0);
          },
        ]}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          height="36px"
          viewBox="0 0 24 24"
          width="36px"
          fill="#FFFFFF"
        >
          <path d="M0 0h24v24H0z" fill="none" />
          <path d="M19 3h-1V1h-2v2H8V1H6v2H5c-1.11 0-1.99.9-1.99 2L3 19c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H5V8h14v11zM7 10h5v5H7z" />
        </svg>
      </button>
      <button
        onClick={() => handleSortBy("price", "asc")}
        className="sort-button reverse"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          enable-background="new 0 0 24 24"
          height="24px"
          viewBox="0 0 24 24"
          width="24px"
          fill="#FFFFFF"
        >
          <rect fill="none" height="24" width="24" />
          <path d="M19.71,9.71L22,12V6h-6l2.29,2.29l-4.17,4.17c-0.39,0.39-1.02,0.39-1.41,0l-1.17-1.17c-1.17-1.17-3.07-1.17-4.24,0L2,16.59 L3.41,18l5.29-5.29c0.39-0.39,1.02-0.39,1.41,0l1.17,1.17c1.17,1.17,3.07,1.17,4.24,0L19.71,9.71z" />
        </svg>
      </button>
      <button
        onClick={() => handleSortBy("price", "desc")}
        className="sort-button"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          height="36px"
          viewBox="0 0 24 24"
          width="36px"
          fill="#FFFFFF"
        >
          <path d="M0 0h24v24H0z" fill="none" />
          <path d="M16 18l2.29-2.29-4.88-4.88-4 4L2 7.41 3.41 6l6 6 4-4 6.3 6.29L22 12v6z" />
        </svg>
      </button>
      <button onClick={toggleRTL} className="button-30 lang">
        {isRTL ? "אנגלית" : "Hebrew"}
      </button>
    </div>
  );
};

export default Options;
